body{
    margin: 0;
    padding: 0;
    background-color: #0fe2aa;
    width: 1200px;
    font-family: 'Sunflower', sans-serif;
}
#todo {
    background-color: #fff;
    width: 400px;
    display: flex;
    flex-direction: column;
    margin: 150px auto;
    justify-content: center;
    align-items: center;
}

input {
    width: 80%;
    height: 25px;
    border: 1px solid #21a57b;
    border-top-right-radius: 10px;
    border-bottom-left-radius: 10px;
    outline: none;
}
button {
    width: 80%;
    margin-top: 10px;
    outline: none;
    border:none;
    font-size: 20px;
    font-family: 'Sunflower', sans-serif;
    cursor: pointer;
    background: lightslategray;
    transition: .3s;

}
button:hover{
    opacity: 0.8;
}
