body{
    margin: 0;
    padding: 0;
    background-color: #485164;
    color: #18427A;
    font-family: 'Shadows Into Light', cursive;
    box-sizing: border-box;
}
#todo {
    background-color: #8E97AA;
    width: 450px;
    display: flex;
    flex-direction: column;
    margin: 150px auto;
    justify-content: center;
    align-items: center;
}

input {
    width: 80%;
    height: 25px;
    border: none;
    border-bottom: 2px solid #6E778A;
    box-shadow: 3px 2px 5px #646C7D;
    outline: none;
    background: #DCE5ED;
    font-size: 15px;
    padding: 5px;
    color: #18427A;
}
button {
    width: 70%;
    margin-top: 10px;
    outline: none;
    border:none;
    font-size: 20px;
    font-family: 'Sunflower', sans-serif;
    cursor: pointer;
    background: #5C6A77;
    box-shadow: 4px 4px 5px #424D56;
    transition: .3s;
    border-radius: 10px;
    color: #FFE74A;
    font-weight: bold;
    margin-top: 20px;
}
button:hover{
    opacity: 0.8;
}
ul {
    flex-direction: column;
    justify-content: flex-start;
    width: 80%;
    margin-top: 25px;
    
}
li {
    font-size: 20px;
    list-style-type: circle;
    justify-content: flex-start;
    margin-bottom: 10px;
    border-bottom: 2px solid #C5D0E6;
    color: #FAFA62;
    width: 95%;
    font-weight: bold;
    position: relative;
    font-family: 'Roboto', sans-serif;
    letter-spacing: 1px;
}
h1 {
    padding: 5px 15px;
    background: #A6B1C7;
    border-top-right-radius: 19px;
    border-bottom-left-radius: 15px;
}
h1 sub {
    font-family: Arial, Helvetica, sans-serif;
    font-size: 12px;
}
ul li span {
    position: absolute;
    width: 18px;
    height: 3px;
    display: inline-block;
    background-color: #4F545F;
    top: 7px;
    right: -28px;
    transition: all .5s;
}
ul li span:hover {
    background-color: #A32C27;
    transform: scale(1.3);
}
